using System;using System.Data;using System.Configuration;using System.Collections;using System.Web;using System.Web.Security;using System.Web.UI;using System.Web.UI.WebControls;using System.Web.UI.WebControls.WebParts;using System.Web.UI.HtmlControls;public partial class share_strotyfromtop : BaseNoneSecurePage{    private bool m_bool = false;    protected void Page_Load(object sender, EventArgs e)    {        if (!IsPostBack)        {            rpt_Category.DataSource = PortalCacheFactory.GetStoryCategory();            rpt_Category.DataBind();        }    }	    protected void Photo_Credits(object sender, EventArgs e) {        Response.Redirect("photocredits.aspx");    }	    protected void Category_OnItemDataBound(object sender, RepeaterItemEventArgs e)    {        HtmlControl _ctr = (HtmlControl)e.Item.FindControl("div_Hold");        Literal _l = (Literal)e.Item.FindControl("tText");        Literal _uSource = (Literal)e.Item.FindControl("uSource");        Literal _flashBDiv = (Literal)e.Item.FindControl("flashBDiv");        Literal _soWrite = (Literal)e.Item.FindControl("soWrite");        StoryCategories s = (StoryCategories)e.Item.DataItem;        _l.Text = "so.addVariable(\"text\",\"" + s.Description + "\")";        _uSource.Text = "so.addVariable(\"uSource\",\"" + s.ID.ToString() + "\")";        _flashBDiv.Text = "<div id=\"header" + s.ID.ToString() + "\" align=\"left\"></div>";        _soWrite.Text = "so.write(\"header"+s.ID.ToString()+"\")";                if (_ctr != null)        {            if (m_bool)            {                _ctr.Style.Add("padding-top", "5px");            }        }        m_bool = true;    }    protected void Category_OnItemCommand(object sender, RepeaterCommandEventArgs e)    {        if(e.CommandName.Equals("ViewStory"))        {            Response.Redirect("storieslist.aspx?source=" + e.CommandArgument.ToString());        }    }    }